package day01;

public class type_정수 {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 3;
	
		int c = a + b;
		int d = a - b;
		int e = a * b;
		int f = a / b;
		int g = a % b;
	
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);
		System.out.println(g);
	
	}
	
}
